export const mergeStreams: MergeStreamsFn;
export type MergeStreamsOptions = {};
export type TMergeStreamsCallback = (err?: Error) => void;
export type MergeStreamsCallbackStyle = (destStream: any, srcStreams: any[], options: MergeStreamsOptions, cb: TMergeStreamsCallback) => void;
export type MergeStreamsPromiseStyle = (destStream: any, srcStreams: any[], options?: MergeStreamsOptions) => Promise<void>;
export type MergeStreamsFn = MergeStreamsCallbackStyle & MergeStreamsPromiseStyle;
